import {singleton} from "@ood/singleton";


@singleton
export class Settings {
    settings = {
        is_reusable_socket_mode_on: true,
        is_stand_by_socket_mode_on: true,
        number_of_stand_by_sockets: 2,
    }

    private convert = i => Boolean(Number(i))

    set({reusableMode, standByMode, numberOfStandBySockets}) {
        this.settings.is_reusable_socket_mode_on =  this.convert(reusableMode);
        this.settings.is_stand_by_socket_mode_on = this.convert(standByMode);
        this.settings.number_of_stand_by_sockets = Number(numberOfStandBySockets);
    }
}

const globalSettings = new Settings()
export default globalSettings;